INSERT INTO neat_liquors (id, liquor_name, brand_name, producer, origin, liquor_type, liquor_age, proof, price, rating, content  ) VALUES
('62e4e030-e3f6-4838-514b-f812767da572', 'Angel''s Envy Kentucky Straight Bourbon', 'Angel''s Envy', 'Louisville Distilling Company, LLC', 'KY, USA', 'Bourbon', '4-6 years', '86.6', '3', '2', 'A light bodied and golden bourbon. A subtle nose with some floral and fruity notes and a light spice.'),
('63e4e030-e4f6-4848-514b-f812767da582', 'Balcones Baby Blue Corn Whisky', 'Balcones', 'Balcones Distilling, LLC', 'TX, USA', 'Whiskey', '4 months', '92', '3', '1', 'An extremely young and thin bodied whiskey, with corn, peanut, and cereal notes. On an interesting note, it is made with ''Hopi'' blue corn, staying true to its Texas identity.'),
('63e5e040-e5f6-4646-514c-f842767da582', 'Balcones Brimstone Texas Scrub Oak Smoked Corn Whisky', 'Balcones', 'Balcones Distilling, LLC', 'TX, USA', 'Whisky', 'Less than 2 years', '106', '3', '1', 'A smokey, corn and mesquite flavor. Mahogany colored and medium bodied, an excellent ingredient for barbeque.'),
('69e5e050-e6f8-4046-524c-f842767da582', 'Balcones Texas Single Malt Whisky', 'Balcones', 'Balcones Distilling, LLC', 'TX, USA', 'Single Malt Whiskey', 'No Age Statement', '105', '4', '2', 'Made with peated malt imported from Scotland and a winner of Britain''s Best in Glass competition. Plenty of smoke and fire but a strong fruity undertone.'),
('68e4e050-e6g8-4146-524c-f842767da582', 'Basil Hayden''s Kentucky Straight Bourbon', 'Basil Hayden''s', 'Beam INC', 'KY, USA', 'Bourbon', '8', '80', '2', '2', 'A light bodied and auburn colored member of the Beam Small Batch Collection, a nice and sweet sipping whiskey. If you are familiar with Old Grand Dad, Basil Hayden''s is in fact the same whiskey aged for a longer time.'),
('52g9m054-f1e9-9736-429s-n523780hf519', 'Blanton''s Original Single Barrel Bourbon', 'Blanton''s', 'Buffalo Trace Distillery', 'KY, USA', 'Bourbon', 'No age statement', '93', '3', '4', 'The world''s first single barrel bourbon expression, Blanton''s  is full bodied, reach, and respectable. With its easily recognized bottle and hints of buttery and toffee, this is a must have in any collection.'),
('62f0k053-h9e1-1737-511a-b425799yf527', 'Buffalo Trace Kentucky Straight Bourbon Whiskey', 'Buffalo Trace', 'Buffalo Trace Distillery', 'KY, USA', 'Bourbon', 'No age statement', '90', '2', '3', 'A nice dry American bourbon with a very reasonable price point for the quality.  Smooth and drinkable, with buttery oak and wheat flavor.'),
('72d1l052-i7e2-3637-612c-v3267c8bs545', 'Bulleit Bourbon Forntier Whiskey', 'Bulleit', 'Bulleit Bourbon, Four Roses Distillery', 'KY, USA', 'Bourbon', '6-8 years', '90', '2', '2', 'Medium bodied and amber colored. Floral and cola notes and a slightly sweet finish. Wonderful when mixed with dark colas.'),
('82s2o051-j6y0-2634-723x-c2277c7gd553', 'Bulleit Rye American Whiskey', 'Bulleit', 'Bulleit Bourbon, MGP Ingredients, INC', 'IN, USA', 'Rye', '4-7 years', '90', '2', '2', 'A solid rye with cloves, vanilla, cinnamon and a zesty bite. Excellent with mixers and light bodied.'),
('82a3p095-k4t9-4534-834z-f1286c7tf471', 'Colonel E. H. Taylor Barrel Proof Straight Kentucky Bourbon Whiskey', 'Colonel E. H. Taylor', 'Buffalo Trace Distillery', 'KY, USA', 'Bourbon', 'No age statement', '134.5', '4', '3', 'A big, bold flavor and a high-proof drink. Full and robust with syrup and almond sweetness and a very firey finish.'),
('72z3p084-k4r8-5533-645q-s9297c5tg688', 'Colonel E. H. Taylor Single Barrel Straight Kentucky Bourbon Whiskey', 'Colonel E. H. Taylor', 'Buffalo Trace Distillery', 'KY, USA', 'Bourbon', '7 years', '100', '4', '4', 'An excellent partner to its higher proof sibling, the single barrel is complex and rich. A full body and a range of sweet and warm spices in the taste along with a smooth finish makes for a very drinkable bourbon.'),
('66x3t072-j4w7-6433-556w-d8208c4th707', 'Elijah Craig Kentucky Straight Bourbon Whiskey 12 Years Old', 'Elijah Craig', 'Heaven Hill Distilleries', 'KY, USA', 'Bourbon', '12', '94', '3', '3', 'Fantastic for the price point with a dry finish and a toasty character. Full bodied and auburn color, a slightly higher proof than its older siblings.'),
('52c3r061-j4e6-7432-457e-c8239c3tj996', 'Elijah Craig Kentucky Straight Bourbon Whiskey 18 Years Old', 'Elijah Craig', 'Heaven Hill Distilleries', 'KY, USA', 'Bourbon', '18', '90', '3', '4', 'An excellent whiskey, smooth and rich with a spicey, lingering finish.'),
('49v3w059-h4r5-4832-368r-a9240c2tk065', 'Elijah Craig Kentucky Straight Bourbon Whiskey 20 Years Old', 'Elijah Craig', 'Heaven Hill Distilleries', 'KY, USA', 'Bourbon', '20', '90', '4', '4', 'Only aged two more years than the 18 variety, yet there is a notable difference in the flavor. Less butter and less sweet, a very dry finish.'),
('30b3w041-g4t4-3833-449t-q0251c9tl144', 'Evan Williams 1783 Kentucky Straight Bourbon Whiskey', 'Evan Williams', 'Heaven Hill Distilleries', 'KY, USA', 'Bourbon', 'No age statement', '86', '2', '2', 'Medium bodied and quite sweet with lingering cinnamon spice. Balanced, but not a stand out drink.'),
('28d3m037-g4y3-6734-588y-j9262c8tq223', 'Evan Williams Kentucky Straight Bourbon Whiskey Black Label', 'Evan Williams', 'Heaven Hill Distilleries', 'KY, USA', 'Bourbon', 'No age statement', '86', '1', '1', 'Solid and reliable as a cocktail liquor. Light bodied and not overpowering.'),
('26w3n026-f4u2-5934-697u-h8213c7tw318', 'Evan Williams Kentucky Straight Bourbon Whiskey Green Label', 'Evan Williams', 'Heaven Hill Distilleries', 'KY, USA', 'Bourbon', 'No age statement', '86','1', '1', 'Like its Black Label twin, a solid and reliable as a cocktail liquor. More acrid and smokey than the Black label, and with a spicier finish.'),
('34a3b024-f4l1-3035-716i-b7213c6te947', 'Four Roses Bourbon', 'Four Roses', 'Four Roses Distillery', 'KY, USA', 'Bourbon', 'No age statement', '80', '1', '2', 'A solid and reputable starter whiskey. Low proof, drinkable, and affordable, light bodied and sweet.'),
('42x3b024-d4k0-2935-815o-m6213c5tr856', 'Four Roses Small Batch Kentucky Bourbon Whiskey', 'Four Roses', 'Four Roses Distillery', 'KY, USA', 'Bourbon', 'No age statement', '90', '2', '3', 'An excellent bourbon with notes of honey and nuts and a spicy finish. Strong value.'),
('51g3v022-d4m9-1836-924p-p5293c4tt365', 'Four Roses Single Barrel Kentucky Straight Bourbon Whiskey', 'Four Roses', 'Four Roses Distillery', 'KY, USA', 'Bourbon', 'No age statement', '100', '2', '2', 'Four Roses'' Single Barrel releases vary quite a bit. Generally they are solid, medium bodied whiskies with good depth and a well earned reputation.'),
('63t3c023-s4j8-1936-863a-t4293c3ty874', 'George Dickel Rye Whisky', 'George Dickel', 'George A. Dickel & Co', 'IN, USA', 'Rye', 'No age statement', '90', '2', '2', 'Finished in the chilled charcoal-mellowed style that makes Dickel whiskey stand out. A solid cocktail rye.'),
('75f3c024-s4n7-2737-652s-z0283c2tu783', 'George Dickel Tennessee Whisky Barrel Select', 'George Dickel', 'George A. Dickel & Co', 'TN, USA', 'Whiskey', 'No age statement', '86', '2', '1', 'Light, sweet, and affordable with a thin mouthfeel.  An excellent backbone for lighter or sour cocktails.'),
('87r3l024-a4b6-3737-541d-s1283c1ti692', 'George Dickel Tennessee Whisky Cascade Hollow', 'George Dickel', 'George A. Dickel & Co', 'TN, USA', '80', '1', '1', 'Light bodied and sour, yet smooth and drinkable.'),
('98d3l025-a4v2-4638-132f-q2273c5to511', 'George Dickel Tennessee Whisky No. 8', 'George Dickel', 'George A. Dickel & Co', 'TN, USA', '80', '1', '1', 'Medicinal with a cherry and vanilla taste and a spicey finish. '),
('86l3k025-e4c3-1538-233g-a3273c8tz410', 'George Dickel Tennessee Whisky No. 12', 'George Dickel', 'George A. Dickel & Co', 'TN, USA', '90', '1', '2', 'A classic Tennessee taste, smooth but very sweet.  Leaves a soft burn on the palate.'),
('84k3j026-r4x4-5531-344h-l4263c7tx329', 'High West Wiskey Double Rye!', 'High West', 'High West Distillery and Saloon', 'UT, USA', 'Rye', 'No age statement', '92', '2', '2', 'A solid rye with a funky, oat filled nose and a spicey and sweet finish. Light bodied but capable.'),
('22j3j021-d4s5-4431-475j-u5253c6tc238', 'High West Whiskey Rendezvous', 'High West', 'High West Distillery and Saloon', 'UT, USA', 'Rye', '6 years', '92', '3', '2', 'A rye for a scotch lover, Rendezvous has a lot of funk and tobacco flavor with a medium body. Like kissing a smoker, in a good way.'),
('39m3h022-s4a4-3332-586k-j4243c5av149', 'Hudson Baby Bourbon', 'Hudson', 'Tuthilltown Spirits', 'NY, USA', 'Bourbon', 'Less than 4 years', '92', '2', '1', 'Very grainy with an overly strong corn palate. A quick finish on this very young bourbon.'),
('59g3h624-a4n3-9233-697l-f3253f4mb146', 'Hudson Four-Grain Bourbon', 'Hudson', 'Tuthilltown Spirits', 'NY, USA', 'Bourbon', 'Less than 4 years', '92', '3', '1', 'Similar to Hudson''s Baby Bourbon, thin bodied and grainy.'),
('39m3f526-m4o0-8134-728l-h2223c8nn147', 'Hudson Manhattan Rye', 'Hudson', 'Tuthilltown Spirits', 'NY, USA', 'Rye', 'Less than 4 years', '92', '3', '1', 'Heavy cereal notes and a brassy flavor. The finish has a slight floral hint and is the best part of the beverage.'),
('49j3h316-n4i9-7135-829z-j1252c7bm249', 'Hudson Single Malt', 'Hudson', 'Tuthilltown Spirits', 'NY, USA', 'Single Malt Whiskey', 'Less than 4 years', '92', '3', '1', 'Many American distilleries have yet to master the single-malt, and this is no exception. The finish is long and bitter.'),
('48n3g127-b4u8-6436-938x-n2243c6vl356', 'Jack Daniel''s Old No. 7 Tennessee Whiskey', 'Jack Daniel''s', 'Jack Daniel Distillery', 'TN, USA', 'Whiskey', 'No age statement', '80', '1', '2', 'Possibly the most famous whiskey in the world. Consistent, reliable, enough flavor to carry itself on ice and smooth enough to be used in a cocktail, yet affordable.'),
('56b3g727-v4y7-5537-837c-b3233c0vk655', 'Jack Daniel''s Old No. 7 Tennessee Whiskey Green Label', 'Jack Daniel''s', 'Jack Daniel Distillery', 'TN, USA', 'Whiskey', 'No age statement', '80', '1', '1', 'A lighter, sweeter version of the standard Black Label.'),
('67v3f828-c4t6-4638-748v-h4223c9xj564', 'Gentleman Jack Rare Tennessee Whiskey', 'Jack Daniel''s', 'Jack Daniel Distillery', 'TN, USA', 'Whiskey', 'No age statement', '80', '2', '1', 'The thick and syrupy member of the Jack family. Strong maple and peper flavors and an almost sticky sweet mouthfeel.'),
('74c3r927-x4e5-4939-646b-r5223c8zh473', 'Jack Daniel''s Single Barrel Select Tennessee Whiskey', 'Jack Daniel''s', 'Jack Daniel Distillery', 'TN, USA', 'Whiskey', 'No age statement', '94', '2', '2', 'The star of Lynchburg, and the head of the Jack family.  Complex, firey heat with sweet notes and a surprisingly bitter finish, an excellent example of Tennessee whiskey.'),
('67x3e328-z4x4-9631-555n-f6313c7lg332', 'Jim Beam Kentucky Straight Bourbon Whiskey', 'Jim Beam', 'Beam Inc.', 'KY, USA', 'Bourbon', '4 years', '80', '1', '2', 'One of the most well known names in American whiskey, and a classic mixer. Well balanced between peppery and sweet flavors, an excellent economy whiskey.'),
('28x3w429-p4s3-8632-454m-o7413c6kf245', 'Jim Beam Kentucky Straight Rye Whiskey', 'Jim Beam', 'Beam Inc.', 'KY, USA', 'Rye', '4 years old', '80', '1', '1', 'A well known rye with a small flavor. Spicy but not complicated, best suited for cocktails.'),
('37x3q520-o4d2-8533-757a-i8513c5jd154', 'Jim Beam Double Aged Kentucky Straight Bourbon Whiskey', 'Jim Beam', 'Beam Inc.', 'KY, USA', 'Bourbon', '8 years', '86', '1', '2', 'The best Beam to be enjoyed alone, this is a light bodied and pleasantly sweet variety. Fruit and honey notes, tawny colored and a cinnamon-sweet finish.'),
('46x3a624-i4f1-7434-365s-u9613c4hs263', 'Jim Beam Devil''s Cut Kentucky Straight Bourbon Whiskey', 'Jim Beam', 'Beam Inc.', 'KY, USA', 'Bourbon', 'No age statement', '90', '2', '1', 'Flowery with a candied flavor, there is no real novelty to make this bottle stand out. '),
('35z3s722-u4v0-6335-675d-y8713c3ga371', 'Knob Creek Small Batch Kentucky Straight Bourbon Whiskey', 'Knob Creek', 'Beam Inc.', 'KY, USA', 'Bourbon', '9 years', '100', '2', '3', 'High quality for the price point, and a nice, full bodied bourbon.  Apricots and cinnmon with a long finish.'),
('44r3d423-y4g8-5536-573f-h8613c2fq482', 'Knob Creek Single Barrel Reserve Kentucky Straight Bourbon Whiskey', 'Knob Creek', 'Beam Inc.', 'KY, USA', 'Bourbon', '9 years', '120', '2', '3', 'As with all the Knob Creek line up, a smooth and excellent whiskey. With a higher proof than its siblings, this bourbon has a slow burn in the finish.'),
('53r3f524-t4b9-4837-417g-n9513c1dw591', 'Knob Creek Rye Whiskey Small Batch', 'Knob Creek', 'Beam Inc.', 'KY, USA', 'Rye', '9 years', '100', '2', '3', 'A classic rye with a medicinal flavor and a medium body.'),
('42t3g625-r4n7-3938-378h-b7313c0se600', 'Masterson''s Straight Rye Whiskey', 'Masterson''s', 'Unknown', 'Canada', 'Rye', '10 years', '90', '4', '4', 'A stellar rye with a full body and an intersting origin. Sourced from Canada by a California brand, the true producer is unknown. Flavors range from honey and floral notes to a warm and cinnamon fire, all on a woody base.'),
('53y3h326-e4h5-2839-269j-v6413c9ar829', 'McCarthy''s Oregon Single Malt Whiskey', 'McCarthy''s', 'Clear Creek Distillery', 'OR, USA', 'Single Malt Whiskey', '3 years', '85', '3', '4', 'An excellent craft whiskey and a rare American whiskey that is an excellent dupe for a true Scotch. Peat, smoke, ash a leather come together for a medium body and a taste thick enough to chew.'),
('64x3j427-w4u3-5731-150k-s5513c8ot048', 'Old Overholt Straight Rye Whiskey', 'Old Overholt', 'Beam Inc.', 'KY, NYC', 'Rye', '4', '80', '1', '2', 'An affordable whiskey with a long history. A buttery, oakey flavor with a cinnamon finish. This rye can easily mix into a cocktail or stand alone.'),
('55y3k522-q4y1-5932-441l-w4613c7iy967', 'Old Potrero Single Malt Rye Whiskey', 'Old Potrero', 'Anchor Brewers and Distillers', 'CA, USA', 'Rye', 'No age statement', '97', '4', '2', 'A funky whiskey with a sticky sweetness that is unique enough to divide everyone who tries it. '),
('66u3k423-l4t2-6633-632q-e4713c6uu886', 'Old Potrero Single Malt 18th Century Style Whiskey', 'Old Potrero', 'Anchor Brewers and Distillers', 'CA, USA', 'Single Malt', 'No age statement', '102.4', '4', '1', 'High priced for a rubbery and unpleasant single malt. Unlikely to appeal to true scotch fans.'),
('75i3j824-k4r0-7934-733w-d3813c5yi715', 'Pikesville Supreme Straight Rye Whiskey', 'Pikesville Supreme', 'Heaven Hill Distilleries', 'KY, USA', 'Rye', '3 years', '80', '1', '2', 'Though its origins and name come from Maryland, this rye has been produced in Kentucky for many years. Full bodied, robust, and flavorful, an excellent starter rye.'),
('64o3h725-j4w8-8235-824e-m6913c4to634', 'Old Rip Van Winkle Handmade Bourbon', 'Old Rip Van Winkle', 'Buffalo Trace Distillery', 'KY, USA', 'Bourbon', '10 years', '107', '3', '4', 'The least expensive of the Van Winkle family, possibly the easiest to find.  Measured amber liquid with a medium body and a hot finish. Any collector or bar would be proud to have this on their shelf.'),
('64x3h725-j4w8-9336-925r-m6913c4tg534', 'Pappy Van Winkle''s Family Reserve 15 Year Old', 'Old Rip Van Winkle', 'Buffalo Trace Distillery', 'KY, USA', 'Bourbon', '15 years', '107', '4', '5', 'One of the most famous and coveted bottles, a Pappy''s is a prestige brand that earns its reputation. A well balanced and ample whiskey, deeply satisfying with sugary notes and a hot finish.'),
('64o3h725-j4w8-0237-636t-j5913c4tf434', 'Pappy Van Winkle''s Family Reserve 20 Year Old', 'Old Rip Van Winkle', 'Buffalo Trace Distillery', 'KY, USA', 'Bourbon', '20 years', '90.4', '4', '4', 'One of the most famous and coveted bottles, a Pappy''s is a prestige brand that earns its reputation. The oakiest of the Pappy family, with a lighter mouthfeel than its siblings.'),
('64i3h725-j4w8-4938-797y-u4913c4td334', 'Pappy Van Winkle''s Family Reserve 23 Year Old', 'Old Rip Van Winkle', 'Buffalo Trace Distillery', 'KY, USA', 'Bourbon', '23 years', '95.6', '4', '5', 'One of the most famous and coveted bottles, a Pappy''s is a prestige brand that earns its reputation. Difficult to find but easily one of the best bourbons available. Full bodied, mellow yet spiced, and the kind of whiskey.'),
('64x3h725-j4w8-1139-598u-y3913c4ts234', 'Van Winkle Family Reserve Rye', 'Old Rip Van Winkle', 'Buffalo Trace Distillery', 'KY, USA', 'Rye', '13 years', '95.6', '4', '5', 'As with all Van Winkle bottles, the rye is an exceptionally good find.  Not particularly sweet, slightly floral, and with a punch of spice on the finish.'),
('64u3h725-j4w8-2931-489i-t1913c4ta134', 'Van Winkle Special Reserve Lot B Kentucky Straight Whiskey', 'Old Rip Van Winkle', 'Buffalo Trace Distillery', 'KY, USA', 'type', '12 years', '90.4', '4', '4', 'An excellent, dark whiskey with a clean and floral feel with a slow burn finish that lingers. Easier to find than the Pappy varieties, but not to be over looked.'),
('73y3g626-h4q6-3532-388o-r2713c3rw253', 'Pine Barrens Single Malt Whiskey', 'Pine Barrens', 'Long Island Spirits', 'NY, USA', 'Single Malt Whiskey', 'less than 2 years', '96', '3', '2', 'A pleasant whiskey but without the usual peat and scotch feel of a single malt. Thin bodied and limited in distribution.'),
('82t3f527-s4d4-4933-277p-e3513c2ee392', 'Riverboat Rye American Rye Whiskey', 'Riverboat Rye', 'MGP Ingredients Inc', 'IN, USA', 'Rye', 'No age statement', '80', '1', '1', 'A mediocre and funky rye with a yeasty flavor and a peppery finish. Thin bodied.'),
('91x3d126-a4s2-5734-676a-z4413c1rr494', 'Russell''s Reserve Kentucky Straight Rye Whiskey', 'Russell''s Reserve', 'Austin Nichols Distilling Co', 'KY, USA', 'Rye', '6 years', '90', '3', '3', 'A mild rye with sweet tobacco flavorings and a nutty finish. Medium bodied and strong vanilla notes.'),
('67r3d127-p4s1-6935-765s-d1413c1ft585', 'Sazerac Straight Rye Whiskey', 'Sazerac', 'Buffalo Trace Distillery', 'KY, USA', 'Rye', '6 years', '90', '2', '4', 'A tasty rye whiskey, more mild and with less heat than generally expected in a rye. An excellent starter rye for the curious.'),
('56e3d128-o4s1-7036-164d-s4413c1ry676', 'Seagram''s Seven Crown American Whiskey', 'Seagram''s 7 Crown', 'Unknown', 'Unknown', 'Whiskey', 'No age statement', '80', '1', '1', 'A blended whiskey with a long history and one of the more well known brands in America. Common in bars as a mixing whiskey, it doesn''t have the legs to stand on its own.'),
('46x3d129-i4s2-8737-253f-q5413c1fu797', 'St. George Single Malt Whiskey', 'St. George Single Malt Whiskey', 'St. George Spirits', 'CA, USA', 'Single Malt Whiskey', 'No age statement', '86', '4', '3', 'A fair interpretation of single malts. Woody, with slight peat and honeysuckle flavors and a soft sherry finish.'),
('37w3d120-u4s3-9538-352g-a6413c1ti868', 'Stranahan''s Colorado Whiskey', 'Stranahan''s', 'Stranahan''s Colorado Whiskey', 'CO, USA', 'Whiskey', '2 years', '94', '3', '2', 'A very funky whiskey that may appeal to scotch fans.  Ash, peanuts, and barely with a spicey finish and a medium body.'),
('31x3d121-y4s4-0931-462h-z6413c1ho959', 'Templeton Rye Small Batch Rye Whiskey', 'Templeton', 'MGP Ingredients, INC', 'IN, USA', 'Rye', 'No age statement', '80', '2', '2', 'A solid and sturdy rye with cereal, vanilla and heat.  Light bodied and an excellent sipping whiskey.'),
('29q3d122-t4s5-8332-514j-x7413c1tn040', 'Westward Oregon Straight Malt Whiskey', 'Westward', 'House Spirits Distillery', 'OR, USA', 'Malt Whiskey', 'No age statement', '90', '3', '2', 'An excellent cocktail whiskey with a light body and a salted caramel flavor with a peppery finish.'),
('59q3d123-r4s6-6133-662k-c7413c1hb938', 'Whipper Snapper Oregon Spirit Whiskey', 'Whipper Snapper', 'Ransom Wine and Spirits', 'OR, USA', 'Whiskey', 'No age statement', '84', '2', '1', 'A blend of corn whiskey and neutral spirits that is thin bodied and herbal.'),
('66a3d124-e4s7-4934-775l-v8413c1tv827', 'Whistlepig Straight Rye Whiskey', 'Whistlepig', 'Whistlepig Farm', 'USA, Canada', 'Rye', '10', '100', '4', '4', 'With mash sourced from Canada and a great team backing it, Whistlepig has earned its place as a coveted whiskey. Flowery with a dry, candy finish and none of the expected rye bitter notes.'),
('75s3d125-w4s8-6935-845z-b5413c1hg716', 'Whistlepid Triple One Rye Whiskey', 'Whistlepig', 'Whistlepig Farm', 'USA, Canada', 'Rye', '11', '111', '4', '5', 'With mash sourced from Canada and a great team backing it, Whistlepig has earned its place as a coveted whiskey. Bright, with apricot and floral notes, dark tea and pepper flavor yet a vanilla finish. An excellent choice for a neat glass.'),
('78d3d126-q4s9-5936-844x-n6413c1yf625', 'Wild Turkey 81 Kentucky Straight Bourbon Whiskey', 'Wild Turkey', 'Austin Nichols Distilling Co', 'KY, USA', 'Bourbon', 'No age statement', '81', '1', '3', 'A light and refreshing bourbon from one of the most famous names in American whiskey. Vanilla, oak, sweet and nutty.'),
('88f3d127-a4s1-4436-853c-m4413c1jd514', 'Wild Turkey 101 Kentucky Straight Bourbon Whiskey', 'Wild Turkey', 'Austin Nichols Distilling Co', 'KY, USA', 'Bourbon', 'No age statement', '101', '2', '2', 'A good and drinkable balance of sweet and bitter, oak and almonds, with a simple sugar finish and a light burn. An excellent mixer.'),
('67g3d128-s4s1-3937-952v-j413c1wgr493', 'Wild Turkey Kentucky Straight Rye Whiskey', 'Wild Turkey', 'Austin Nichols Distilling Co', 'KY, USA', 'Rye', 'No age statement', '101', '2', '1', 'A floral and earthy rye that is improved with a splash of water. A long and spicy burn on the finish.'),
('97h3d129-d4s2-2938-711b-h8413c1fa384', 'Wild Turkey Rare Breed Kentucky Straight Bourbon Whiskey', 'Wild Turkey', 'Austin Nichols Distilling Co', 'KY, USA', 'Bourbon', '6', '108', '2', '2', 'A fair and respectable bourbon for the price point with a honey finish and a medium body.'),
('56c3d121-f4s3-6938-637n-g5413c1tr275', 'Lagavulin 16 Year', 'Lagavulin', 'Lagavulin Whisky Distillery', 'Islay, Scotland', 'Scotch', '16 years', '86', '2', '3', 'An excellent example of a true, peat driven scotch. Intense smoke with a caramel finish.'),
('45x3d122-g4s1-7139-536m-f3413c1gg166', 'The Glenlivet 12 Year Old Single Malt Scotch Whisky', 'The Glenlivet', 'Glenlivet Distillery', 'origin', 'Scotch', '12 years', '86', '2', '3', 'Fruity and summer for a scotch. An easy starter scotch for those not ready to jump in with a smokey variety, it has a creamy finish.'),
('45c3d123-h4s5-5239-425a-d2413c1fh057', 'The Glenlivet 15 Year Old Single Malt Scotch Whisky', 'The Glenlivet', 'Glenlivet Distillery', 'origin', 'Scotch', '15 years', '86', '2', '3', 'A smooth scotch with a lingering spice finish. A fantastic expression of scotch''s softer side.'),
('35d3d124-c4s6-4331-359s-s4413c1dj948', 'The Glenlivet 18 Year Old Single Malt Scotch Whisky', 'The Glenlivet', 'Glenlivet Distillery', 'origin', 'Scotch', '18 years', '86', '3', '4', 'The expression to win the most awards, a solid mid-range bottle. Toffee and spice flavors and an ample body.'),
('74f3d125-v4s7-5439-258d-e5413c1sp849', 'The Glenlivet 21 Year Old Single Malt Scotch Whisky', 'The Glenlivet', 'Glenlivet Distillery', 'origin', 'Scotch', '21 years', '86', '4', '5', 'A unique bottle with a sticky toffee mouthfeel, well suited to drinking neat with a full flavored meal.'),
('93f3d126-b4s8-3538-117f-r6413c1rl790', 'The Glenlivet 25 Year Old Single Malt Scotch Whisky', 'The Glenlivet', 'Glenlivet Distillery', 'origin', 'Scotch', '25 years', '86', '4', '4', 'A more rare bottle and the depth of the flavor makes finding it worth it. A rich and complex drink, best enjoyed alone, perhaps as a nightcap or after dinner. You want a fresh palate to enjoy the subtle notes this scotch has to offer.'),
('86g3d127-n4s9-1633-116g-t7413c1ei638', 'Aberlour 12 Year Old Single Malt Scotch Whisky', 'Aberlour', 'Aberlour Distillery', 'Scotland', 'Scotch', '12', '86', '2', '3', 'Oloroso casks impart subtle yet sweet notes of dried fruits, a capable scotch on the sweeter side.'),
('75x3d128-m4s5-3733-215h-y9413c1wk597', 'Aberlour 16 Year Old Single Malt Scotch Whisky', 'Aberlour', 'Aberlour Distillery', 'Scotland', 'Scotch', '16', '86', '2', '3', 'Aged for a minimum of 16 years in a combination of ex-bourbon and ex-sherry casks, almonds and baking spices on the palate. Finish is warm and spicy.'),
('63j3d129-m4s2-4834-274j-u0413c1dj436', 'Aberlour 18 Year Old Single Malt Scotch Whisky', 'Aberlour', 'Aberlour Distillery', 'Scotland', 'Scotch', '18', '86', '3', '4', 'Similarly sweet to the other bottles in this family, but with the introduction of the more traditional tobacco and earthy flavors associated with scotch.'),
('52k3d120-n4s3-5935-373k-i1413c1vu365', 'Aberlour A''bunadh Single Malt Scotch Whisky', 'Aberlour', 'Aberlour Distillery', 'Scotland', 'Scotch', 'No age statement', '119', '2', '4', 'In the Aberlour family the 12 Year old is extremely smooth, but the A''bunadh is cask strength, it''s not for the laymen.'),
('46i3d121-b4s2-8835-364l-z2413c1by254', 'Aberlour Casg Annamh Batch 1 Single Malt Scotch Whisky', 'Aberlour', 'Aberlour Distillery', 'Scotland', 'Scotch', 'No age statement', '96', '2', '4', 'A softer single malt, with a lower proof that the A''bunadh.'),
('91y3d122-v4s8-9736-455q-p3413c1nh133', 'The Dead Rabbit Irish Whiskey', 'The Dead Rabbit Irish Whiskey', 'Dublin Liberties Distillery', 'Ireland', 'Irish Whiskey', '5 years', '88', '2', '3', 'Vanilla flavor that is a bit like a bourbon, this whiskey blend was named for the notorious Irish gang from New York. Excellent for an Irish cocktail or in coffee.'),
('29x3d124-c4s9-1637-446w-k4513c1yn042', 'Jameson Irish Whiskey', 'Jameson Irish Whiskey', 'Irish Distillers', 'Ireland', 'Irish Whiskey', '4-7 years', '80', '2', '3', 'The best selling Irish whiskey in the world and an easily recognized brand. Best used as a mixer, excellent with ginger ale.'),
('38y3d123-x4s9-2532-517e-l7413c1ug971', 'Yoichi Single Malt', 'Nikka', 'Nikka Whisky', 'Japan', 'Japanese Whiskey', 'No age statement', '90', '2', '3', 'A strong and peaty single malt that reminds one of a scotch, which come at no suprise as it is distilling in the norternmost regions of Japan, where the weather is no unlike the highlands. An excellent whiskey from Japan made with coal filtered distillation.'),
('47t3d122-p4s8-3433-618r-z9413c1it869', 'Taketsuru Pure Malt 17 Years Old', 'Nikka', 'Nikka Whisky', 'Japan', 'Japanese Whiskey', '17', '86', '4', '4', 'A mellow and well rounded bottle that comes with a high price, but a true collector could find it worth the cost. The peat builds up over time, and this is a smooth yet deep flavored and rich drink.'),
('56r3d127-o4s7-4332-629t-m9413c1of758', 'Taketsuru Pure Malt 21 Years Old', 'Nikka', 'Nikka Whisky', 'Japan', 'Japanese Whiskey', '21', '86', '4', '5', 'The prestige bottle of Japan, the 21 year is extremely smooth, well balanced, and a pure pleasure to drink. Put this next to a bottle of Pappy Van Winkle''s to impress your guests.'),
('95w3d126-i4s7-5234-534y-n8413c1pr687', 'Taketsuru Pure Malt', 'Nikka', 'Nikka Whisky', 'Japan', 'Japanese Whiskey', 'No age statement', '86', '2', '3', 'A really solid bottle of Japanese whiskey for anyone not prepared to pay for the 17 or 21 year bottles. At hundreds less, this is a much more affordable and still very respectable bottle.'),
('84q3d125-u4s6-6135-443u-b7413c1ae576', 'Nikka Coffey Grain Whisky', 'Nikka', 'Nikka Whisky', 'Japan', 'Japanese Whiskey', 'No age statement', '90', '2', '4', 'Predominantly made from corn and distilled in a Coffey Still, not to be confused with coffee. A beautiful grain whiskey and very drinkable with a lower price point than many Nikka varieties.'),
('73a3d129-y4s6-7236-352i-v6413c1sd485', 'The Nikka 12 Years Old', 'Nikka', 'Nikka Whisky', 'Japan', 'Japanese Whiskey', '12 years', '86', '2', '4', 'A solid option for someone looking for a mid-grade Nikka product. Using more malt whiskies than grain whiskies in the formula, this expression has malty and rich flavors.'),
('68x3d128-t4s9-8337-261o-c5413c1dc394', 'The Yamazaki Single Malt Japanese Whisky 12 Year', 'Yamazaki Whisky', 'Suntory Whisky', 'Japan', 'Japanese Whiskey', '12', '86', '3', '4', 'A golden liquid with sweet fruits and gingers with a softer finish. An excellent first whiskey to try from the oldest malt distillery in Japan.'),
('58c3d127-r4s8-9438-174p-x4413c1fd273', 'The Yamazaki Single Malt Japanese Whisky 18 Year', 'Yamazaki Whisky', 'Suntory Whisky', 'Japan', 'Japanese Whiskey', '18', '86', '4', '5', 'An award winning single malt, a truly stunning combination of sherry, oak, and smoke.'),
('49v3d126-e4s7-1539-785a-d3413c1gw162', 'The Yamazaki Single Malt Japanese Whisky 25 Year', 'Yamazaki Whisky', 'Suntory Whisky', 'Japan', 'Japanese Whiskey', '25', '86', '4', '4', 'An incredibly hard bottle to find, even an empty bottle can go for over a thousand USD. You read that right, an EMPTY bottle can cost that much. Supposedly the flavor is deep and complex, but I have yet to meet anyone who has even held a bottle much less verified the taste.'),
('39b3d125-w4s5-2631-696s-s2413c1he031', 'Hibiki Japanese Harmony', 'Suntory Whisky Hibiki', 'Suntory Whisky', 'Japan', 'Japanese Whiskey', 'No age statement', '86', '2', '3', 'A subtle and softer member of the Suntory brands, this expression is a blend with honey and floral notes. The easiest to find of the Hibiki family.'),
('50n3d124-q4s4-3732-887d-a4413c1jr940', 'Hibiki 17 Year', 'Suntory Whisky Hibiki', 'Suntory Whisky', 'Japan', 'Japanese Whiskey', '17 years', '86', '4', '4', 'A world renowned Japanese blend, Hibiki 17 Year Old is made with aged malt and grain whiskies from Suntory''s distilleries. Initially released in 1989, the blend is matured in Japanese mizunara oak casks at Yamazaki.'),
('61x3d128-k4s5-4833-898f-q5413c1kt829', 'Hibiki 21 Year', 'Suntory Whisky Hibiki', 'Suntory Whisky', 'Japan', 'Japanese Whiskey', '21 years', '86', '4', '5', 'Another stellar and long-aged whiskey from Suntory, and a rare bottle to find. Expect to pay for the pleasure, but the malt and grain flavors and the prestige may be worth it to some.'),
('72j3d124-j4s4-5834-789g-w6413c1ly738', 'Glenfiddich 12 Year Old', 'Glenfiddich', 'Glenfiddich Whisky Distillery', 'Scotland', 'Scotch', '12', '80', '1', '3', 'Creamy with a long, smooth and mellow finish, the base of the Glenfiddich family of expressions.'),
('93k3d125-g4s5-6735-678h-e7413c1zu647', 'Glenfiddich 15 Year Old', 'Glenfiddich', 'Glenfiddich Whisky Distillery', 'Scotland', 'Scotch', '15', '80', '2', '3', 'A spicy and solid scotch, warming and yet mellow at the same time. An excellent sipping scotch.'),
('24l3d123-g4s7-7636-567j-r8413c1xh556', 'Glenfiddich 18 Year Old', 'Glenfiddich', 'Glenfiddich Whisky Distillery', 'Scotland', 'Scotch', '18', '80', '2', '3', 'Fruity, robust, and a distinguished finish. The oak notes are a nice touch, and this is a solid choice for the average collector that would like a nicer bottle.'),
('25o3d122-f4s7-8937-456k-t9413c1cj465', 'Glenfiddich 21 Year Old', 'Glenfiddich', 'Glenfiddich Whisky Distillery', 'Scotland', 'Scotch', '21', '80', '3', '3', 'Finished in rum casks, this is an unusual expression with a warm and spicy feel. There is definitely a hint of the tropical origins in this bottle.'),
('31i3d126-d4s3-9638-545l-y1413c1vk374', 'Glenfiddich 30 Year Old', 'Glenfiddich', 'Glenfiddich Whisky Distillery', 'Scotland', 'Scotch', '30', '86', '4', '4', 'Not nearly as difficult to come by as the 40 and 50 year expressions, but also possibly not worth as much of the hype. Heart oak and a warm mouthfeel.'),
('47u3d125-s4s2-7538-934q-u2413c1bl283', 'Glenfiddich 40 Year Old', 'Glenfiddich', 'Glenfiddich Whisky Distillery', 'Scotland', 'Scotch', '40', 'Depends on year', '4', '4', 'Similar to the 50 year, this is an exceptionally rare bottle. A gold medal winner and sought after by experts, only 1000 bottles are released in a year.'),
('91x3d122-a4s3-6939-235z-i3413c1np192', 'Glenfiddich 50 Year Old', 'Glenfiddich', 'Glenfiddich Whisky Distillery', 'Scotland', 'Scotch', '50', '92', '4', '5', 'Only 50 bottles of this single malt Scotch whisky are released each year. A gentle, smokey whiskey and an unusual expression to say the least.');