INSERT INTO neat_liquors (id, liquor_name, brand_name, producer, origin, liquor_type, liquor_age, proof, price, rating, content  ) VALUES
('62e4e030-e3f6-4838-514b-f812765df572', 'Angel''s Envy Kentucky Straight Bourbon', 'Angel''s Envy','Louisville Distilling Company, LLC', 'KY, USA', 'Bourbon', '4-6 years', '86.6', '3', '2', 'A light bodied and golden bourbon. A subtle nose with some floral and fruity notes and a light spice.'),
('12i5c157-a2e7-4938-814w-r8217c5da582', 'Balcones Baby Blue Corn Whisky', 'Balcones', 'Balcones Distilling, LLC', 'TX, USA', 'Whiskey', '4 months', '92', '3', '1', 'An extremely young and thin bodied whiskey, with corn, peanut, and cereal notes. On an interesting note, it is made with ''Hopi'' blue corn, staying true to its Texas identity.'),
('22k6v056-b3e1-4938-814q-g8237c5dk593', 'Balcones Brimstone Texas Scrub Oak Smoked Corn Whisky', 'Balcones','Balcones','Balcones Distilling, LLC', 'TX, USA', 'Whisky', 'Less than 2 years', '106', '3', '1', 'A smokey, corn and mesquite flavor. Mahogany colored and medium bodied, an excellent ingredient for barbeque.'),
('32j7b055-d4e5-4938-814b-f8327c5dj505', 'Balcones Texas Single Malt Whisky', 'Balcones', 'Balcones','Balcones Distilling, LLC', 'TX, USA', 'Single Malt', 'No Age Statement', '105', '4', '2', 'Made with peated malt imported from Scotland and a winner of Britain''s Best in Glass competition. Plenty of smoke and fire but a strong fruity undertone.'),
('42h8n055-c3e3-4938-814d-d8427c5dn580', 'Basil Hayden''s Kentucky Straight Bourbon', 'Basil Hayden''s','Beam INC', 'KY, USA', 'Bourbon', '8', '80', '2', '2', 'A light bodied and auburn colored member of the Beam Small Batch Collection, a nice and sweet sipping whiskey. If you are familiar with Old Grand Dad, Basil Hayden''s is in fact the same whiskey aged for a longer time.'),
('52g9m054-f1e9-4938-814s-n8237c5hf519', 'Blanton''s Original Single Barrel Bourbon', 'Blanton''s', 'Buffalo Trace Distillery', 'KY, USA', 'Bourbon', 'No age statement', '93', '3', '4', 'The world''s first single barrel bourbon expression, Blanton''s  is full bodied, reach, and respectable. With its easily recognized bottle and hints of buttery and toffee, this is a must have in any collection.'),
('62f0k053-h9e1-4938-814a-b8257c5yf527', 'Buffalo Trace Kentucky Straight Bourbon Whiskey', 'Buffalo Trace','Buffalo Trace Distillery', 'KY, USA', 'Bourbon', 'No age statement', '90', '2', '3', 'A nice dry American bourbon with a very reasonable price point for the quality.  Smooth and drinkable, with buttery oak and wheat flavor.'),
('72d1l052-i7e2-4938-814c-v8267c5bf545', 'Bulleit Bourbon Forntier Whiskey', 'Bulleit', 'Bulleit Bourbon, Four Roses Distillery', 'KY, USA', 'Bourbon', '6-8 years', '90', '2', '2', 'Medium bodied and amber colored. Floral and cola notes and a slightly sweet finish. Wonderful when mixed with dark colas.'),
('82s2o051-j6y0-4938-814x-c8277c5gf553', 'Bulleit Rye American Whiskey', 'Bulleit', 'Bulleit Bourbon, MGP Ingredients, INC', 'IN, USA', 'Rye', '4-7 years', '90', '2', '2', 'A solid rye with cloves, vanilla, cinnamon and a zesty bite. Excellent with mixers and light bodied.'),
('82a3p091-k4t8-4938-814z-f8286c5tf471', 'Colonel E. H. Taylor Barrel Proof Straight Kentucky Bourbon Whiskey', 'Colonel E. H. Taylor', 'Buffalo Trace Distillery', 'KY, USA', 'Bourbon', 'No age statement', '134.5', '4', '3', 'A big, bold flavor and a high-proof drink. Full and robust with syrup and almond sweetness and a very firey finish.'),
('72z3p081-k4r8-4938-814z-s8297c5tf688', 'Colonel E. H. Taylor Single Barrel Straight Kentucky Bourbon Whiskey', 'Colonel E. H. Taylor', 'Buffalo Trace Distillery', 'KY, USA', 'Bourbon', '7 years', '100', '4', '4', 'An excellent partner to its higher proof sibling, the single barrel is complex and rich. A full body and a range of sweet and warm spices in the taste along with a smooth finish makes for a very drinkable bourbon.'),
('62x3p071-k4e8-4938-814z-d8208c5tf707', 'Elijah Craig Kentucky Straight Bourbon Whiskey 12 Years Old', 'Elijah Craig', 'Heaven Hill Distilleries', 'KY, USA', 'Bourbon', '12', '94', '3', '3', 'Fantastic for the price point with a dry finish and a toasty character. Full bodied and auburn color, a slightly higher proof than its older siblings.'),
('52c3p061-k4w8-4938-814z-c8239c5tf996', 'Elijah Craig Kentucky Straight Bourbon Whiskey 18 Years Old', 'Elijah Craig', 'Heaven Hill Distilleries', 'KY, USA', 'Bourbon', '18', '90', '3', '4', 'An excellent whiskey, smooth and rich with a spicey, lingering finish.'),
('42v3p051-k4q8-4938-814z-a8240c5tf065', 'Elijah Craig Kentucky Straight Bourbon Whiskey 20 Years Old', 'Elijah Craig', 'Heaven Hill Distilleries', 'KY, USA', 'Bourbon', '20', '90', '4', '4', 'Only aged two more years than the 18 variety, yet there is a notable difference in the flavor. Less butter and less sweet, a very dry finish.'),
('32b3p041-k4d8-4938-814z-q8251c5tf144', 'Evan Williams 1783 Kentucky Straight Bourbon Whiskey', 'Evan Williams', 'Heaven Hill Distilleries', 'KY, USA', 'Bourbon', 'No age statement', '86', '2', '2', 'Medium bodied and quite sweet with lingering cinnamon spice. Balanced, but not a stand out drink.'),
('22n3p031-k4s8-4938-814z-e8262c5tf223', 'Evan Williams Kentucky Straight Bourbon Whiskey Black Label', 'Evan Williams', 'Heaven Hill Distilleries', 'KY, USA', 'Bourbon', 'No age statement', '86', '1', '1', 'Solid and reliable as a cocktail liquor. Light bodied and not overpowering.'),
('12m3p021-k4a8-4938-814z-x8213c5tf312', 'Evan Williams Kentucky Straight Bourbon Whiskey Green Label', 'Evan Williams', 'Heaven Hill Distilleries', 'KY, USA', 'Bourbon', 'No age statement', '86','1', '1', 'Like its Black Label twin, a solid and reliable as a cocktail liquor. More acrid and smokey than the Black label, and with a spicier finish.'),
('12m3p021-k4a8-4938-814z-x8213c5tf312', 'Four Roses Bourbon', 'Four Roses', 'Four Roses Distillery', 'KY, USA', 'Bourbon', 'No age statement', '80', '1', '2', 'A solid and reputable starter whiskey. Low proof, drinkable, and affordable, light bodied and sweet.'),
('12m3p021-k4a8-4938-814z-x8213c5tf312', 'Four Roses Small Batch Kentucky Bourbon Whiskey', 'Four Roses', 'Four Roses Distillery', 'KY, USA', 'Bourbon', 'No age statement', '90', '2', '3', 'An excellent bourbon with notes of honey and nuts and a spicy finish. Strong value.'),
('12m3p021-k4a8-4938-814z-x8213c5tf312', 'Four Roses Single Barrel Kentucky Straight Bourbon Whiskey', 'Four Roses', 'Four Roses Distillery', 'KY, USA', 'Bourbon', 'No age statement', '100', '2', '2', 'Four Roses'' Single Barrel releases vary quite a bit. Generally they are solid, medium bodied whiskies with good depth and a well earned reputation.'),
('12m3p021-k4a8-4938-814z-x8213c5tf312', 'George Dickel Rye Whisky', 'George Dickel', 'George A. Dickel & Co', 'IN, USA', 'Rye', 'No age statement', '90', '2', '2', 'Finished in the chilled charcoal-mellowed style that makes Dickel whiskey stand out. A solid cocktail rye.'),
('12m3p021-k4a8-4938-814z-x8213c5tf312', 'George Dickel Tennessee Whisky Barrel Select', 'George Dickel', 'George A. Dickel & Co', 'TN, USA', 'Whiskey', 'No age statement', '86', '2', '1', 'Light, sweet, and affordable with a thin mouthfeel.  An excellent backbone for lighter or sour cocktails.'),
('12m3p021-k4a8-4938-814z-x8213c5tf312', 'George Dickel Tennessee Whisky Cascade Hollow', 'George Dickel', 'George A. Dickel & Co', 'TN, USA', '80', '1', '1', 'Light bodied and sour, yet smooth and drinkable.'),
('12m3p021-k4a8-4938-814z-x8213c5tf312', 'George Dickel Tennessee Whisky No. 8', 'George Dickel', 'George A. Dickel & Co', 'TN, USA', '80', '1', '1', 'Medicinal with a cherry and vanilla taste and a spicey finish. '),
('12m3p021-k4a8-4938-814z-x8213c5tf312', 'George Dickel Tennessee Whisky No. 12', 'George Dickel', 'George A. Dickel & Co', 'TN, USA', '90', '1', '2', 'A classic Tennessee taste, smooth but very sweet.  Leaves a soft burn on the palate.'),
('12m3p021-k4a8-4938-814z-x8213c5tf312', 'High West Wiskey Double Rye!', 'High West', 'High West Distillery and Saloon', 'UT, USA', 'Rye', 'No age statement', '92', 'price', '2', 'A solid rye with a funky, oat filled nose and a spicey and sweet finish. Light bodied but capable.'),
('12m3p021-k4a8-4938-814z-x8213c5tf312', 'High West Whiskey Rendezvous', 'High West', 'High West Distillery and Saloon', 'UT, USA', 'Rye', '6 years', '92', 'price', '2', 'A rye for a scotch lover, Rendezvous has a lot of funk and tobacco flavor with a medium body. Like kissing a smoker, in a good way.'),
